#define RESPONSE_INDEX "{\"info\": \"Use it!\"}"
#define TEST_FORM_SRC "<!doctype html><html lang=\"en\"><head> <meta charset=\"UTF-8\"> <meta name=\"viewport\" content=\"width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0\"> <meta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\"> <title>API for libpostal</title> <link rel=\"stylesheet\" href=\"https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.5.2/css/bootstrap.min.css\"></head><body class=\"container\"><br><br><br><h1 class=\"h3\">API for libpostal</h1><form action=\"\" method=\"get\"> <div class=\"form-group\"> <label for=\"address\">Type an address</label> <textarea name=\"address\" class=\"form-control\" id=\"address\" cols=\"30\" rows=\"10\"></textarea> </div> <input type=\"submit\" id=\"send\" class=\"btn btn-success\" value=\"Get JSON\"></form><br><pre id=\"result\" class=\"alert alert-info\"></pre><script src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery/3.5.1/jquery.min.js\"></script><script> var apiURL = 'http://127.0.0.1:8080'; function addResult(json) { $('#result').html(json); } addResult('Here will be a result'); $('#send').click(function (e) { e.preventDefault(); var address = $('#address').val(); $.ajax({ url: apiURL + '/parse', type: 'GET', async: true, crossDomain: true, headers: { 'X-To-Parse': encodeURIComponent(address) }, success: function (data) { addResult(JSON.stringify(data, null, 4)); }, error: function (e) { addResult(JSON.stringify(e.message, null, 4)); } }); });</script></body></html>"
